#include <iostream>
#include <sstream>
#include <string>
#include <vector>

using namespace std;


class Solution {
public:
	string validIPAddress(string IP) {
		int l = IP.size();
		istringstream input(IP);
		string sNum;
		int n;

		if (IP.find('.') != IP.npos) {  // ipv4

			for (int i = 0; i < 4; ++i) {
				if (input.eof() == true) return "Neither";
				getline(input, sNum, '.');
				if (sNum[0] == '0'&& sNum.size() != 1) return "Neither";
				for (auto it : sNum) if (!('0' <= it && it <= '9')) return "Neither";
				if (sNum.size() <= 0) return "Neither";
				if (sNum.size() > 3) return "Neither";
				n = stoi(sNum);
				if (n < 0 || 255 < n) return "Neither";
			}
			if (input.eof() != true) return "Neither";

			return "IPv4";

		}
		else {							// ipv6
			for (int i = 0; i < 8; ++i) {
				getline(input, sNum, ':');
				if (sNum.size() <= 0) return "Neither";
				if (sNum.size() > 4) return "Neither";

				for (auto it : sNum) {
					if (!(('0' <= it && it <= '9') ||
						('a' <= it && it <= 'f') ||
						('A' <= it && it <= 'F'))) return "Neither";
				}

			}
			if (input.eof() != true) return "Neither";
			return "IPv6";
		}

		return "Neither";
	}
};

int main() {
	char c;
	Solution sol;
	string s = "..."; //
	//"12.12.12"; //  "12..33.4"; //  "1a2.16.254.1";
	//string s = "2001:0db8:85a3:0:0:8A2E:0370:7334";

	cout << sol.validIPAddress(s);

	cin >> c;
}